    team_name = team_block.find('i').get_text()
    team_br = team_block.find_all('br')

    mapped = list(map(lambda x: x.nextSibling.string, team_br))
    filtered = list(filter(lambda x: x is not ' ', mapped))

    rider_items = [sanitize_string(team_name, x)
                   for x in mapped
                   if x is not ' ']

    return rider_items


