def sanitize_string(team: str, x: str):
    if x == ' ':
        return None
    else:
        name = x.replace('.', '').split(' ')[1:]

        rider_url = 'https://www.procyclingstats.com/en/rider/{}-{}'.format(name[1].lower(), name[2].lower())
        rider = Rider(team, name[0], name[1], name[2], rider_url)
        return rider


